mapper.select:
SELECT vr.*, c.real_name as customer_name, c.phone as customer_phone,
       h.house_no, h.description as house_desc, h.layout, h.house_type as houseType,
       u.real_name as sales_name
FROM tb_view_record vr
LEFT JOIN tb_customer c ON vr.customer_id = c.id
LEFT JOIN tb_house h ON vr.house_id = h.id
LEFT JOIN tb_user u ON vr.sales_id = u.id
WHERE 1=1
 AND vr.customer_id = #{query.customerId} 
 AND vr.house_id = #{query.houseId} 
 AND vr.sales_id = #{query.salesId} 
 AND vr.view_time >= #{query.startTime} 
 AND vr.view_time <= #{query.endTime} 
 AND vr.status = #{query.status} 
 AND vr.appoint_type = #{query.appointType} 
ORDER BY vr.view_time DESC
